import {useState, useEffect} from 'react';

export default function MessagesBox({text, role, name, handleChange}){
    const [textState, setTextState] = useState(text)

    useEffect(() => {
        setTextState(text);
    }, [text]);

    const changeIputValue = (event) => {
        const newValue = event.target.value;
        setTextState(newValue)
        handleChange(name, newValue)
    }

    return(
        <>
            <h1>{role}</h1>
            <span>{name}</span>
            <textarea 
                name={name}
                onBlur={(event) => changeInputValue(event.target.value)}
                value={textState}
                rows='20'
                cols='200'
            ></textarea>
        </>
    )
}
