import {useState} from 'react'

export default function MessagesBox(props){
    const [text, setText] = useState(props.text)

    function handleChange(event) {
        setText(event.target.value) 
    }

    return(
        <>
            <h1>{props.role}</h1>
            <textarea 
                name={props.name}
                onChange={handleChange}
                value={text}
                rows='20'
                cols='200'
            ></textarea>
        </>
    )
}
